import React, { Component } from 'react';

class Mahshevon extends Component {
    state = { score: 0 }
    num1 = React.createRef()
    num2 = React.createRef()


    onMultiClick = () => {
        let n1 = this.num1.current.value;
        let n2 = this.num2.current.value;

        this.setState({ score: n1 * n2 })
    }
    render() {
        return (
            <div className ="mt-4">
                Mahshevon
                <br />
                <input ref={this.num1} type="number" />
                <input ref={this.num2} type="number" />

                <br />
                <button onClick={this.onMultiClick}>multi</button>
                <h2>Score: {this.state.score}</h2>
            </div>
        )
    }
}

export default Mahshevon
