import React,{Component} from "react";

class Gallery extends Component{
    counter = 0;

  series_ar = [
    { name: "The simpons", imgUrl: "simpsons.jpg" },
    { name: "Dexter", imgUrl: "dexter.jpg" },
    { name: "South park", imgUrl: "south.jpg" },
    { name: "Family guy", imgUrl: "familyguy.png" },
  ]

  onNextClick = () => {
    this.counter++;
    this.forceUpdate();
  }

  onBackClick = () => {
    this.counter--;
    if(this.counter < 0){
      this.counter = this.series_ar.length-1
    }
    this.forceUpdate()
  }

  render() {
    return (
      <div className="text-center">
        <img src={"images/" + this.series_ar[this.counter%this.series_ar.length].imgUrl} />
        <h2>{this.series_ar[this.counter%this.series_ar.length].name}</h2>
        <button onClick={this.onBackClick}>Back</button>
        <button onClick={this.onNextClick}>Next</button>
      </div>

    )
  }
}


export default Gallery;
