import React,{Component} from "react";

class CoounterState extends Component{
    state = {counter:0,use:"koko"}
    onPulsClick = () =>{
        // this.state.counter++;
        // this.forceUpdate();
        this.setState({counter:this.state.counter+1})

    }
  render(){
    return(
      <div>
        <h2> Counter: {this.state.counter}</h2>
        <button onClick = {this.onPulsClick}>Plus</button>
      </div>
    
    )
  }
}

export default CoounterState;
