import React, { useRef, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useHistory } from 'react-router-dom';
import Moment from 'moment';
function TodoTitle(props){
  let todoName = useRef(null);
  let history =useHistory();

  let counterId = useSelector((myStore) => myStore.counterId)
  let dispatch = useDispatch()
  let dateFormat = Moment(new Date()).format('DD/MM/YYYY');

  const addTask = () => {
    dispatch({ type: "addCounterId" });
    // alert(userInput.current.value);
    let bodyData = {
      id: counterId,
      task_name: "todos shopping list",
      name: userInput.current.value,
      items: [{test:"123"}],
      created_at: dateFormat,
      updated_at:dateFormat
    }
    dispatch({ type: "addTask", taskItem:bodyData})
    userInput.current.value="";
  }

  const addTodo =()=>{
    alert(todoName.current.value)
    history.push("/addItems/");
  }
  return(
    <div className="container text-center">  
    <h1 className="display-3 mb-3" >named todo title</h1>
     <div className="d-flex align-items-center justify-content-center">
        <input ref={todoName} type="text" className="form-control w-25 border border-info" placeholder="enter todo name..." />
        <button onClick={addTodo} className="btn btn-primary">Apply</button>
      </div>
    </div> 
  )
}

export default TodoTitle
