import React from 'react';
function ToDoItem(props) {
  let item = props.item.items;

  return (
    <div className="border border-success p-2 w-75">
      <h1 className="display-4 text-center" style={{ color: "turquoise" }}>{props.item.todo_name}</h1>
      {item.map((val, i) => {
        return (
          <div key={i} className="d-flex  justify-content-around  align-items-center border border-danger p-2 m-2">
            <div className="col-lg-6">
              <input id={val.id} type="checkbox" name="isDone" className="mr-1" checked={val.completed} />
              <label htmlFor={val.id}>{val.item_name}</label>
            </div>
            <div>{val.created_at}</div>
            <div className="col-lg-6">
              <button onClick={() => { alert(val.id) }} className="btn btn-outline-secondary">X</button>
            </div>
          </div>)

      })}
    </div>
  )
}

export default ToDoItem
