        this.setState({ vip_shop: prods_ar })
    }
    render() {
        console.log("prods_ar", prods_ar);
        return (
            <div className="container my-5">
                <div className="row">
                    {this.state.vip_shop.map(item => {
                        return (
                            <div key={item.name} className="col-lg-6 border">
                                <img src={item.image} className="float-left mr-2 w-25" />
                                <h3>{item.name}</h3>
                                <div> Price : {item.price}</div>
                            </div>
                        )
                    })}

