import React, { useEffect, useRef, useState } from 'react';
import { useDispatch } from 'react-redux';
import "../css_comps/todos.css"
import Moment from 'moment';
function ToDoItem(props) {
  let userInput = useRef(null);
  let dateFormat = Moment(new Date()).format('DD/MM/YYYY');
  let [item,setItem] = useState(props.item.items)

  // useEffect (()=>{
  //   console.log("item123->",props.item.items);
  //   setItem(props.item.items)
  // },[props.item.items.length])

  let dispatch = useDispatch();
  const addTask = () => {
    let tempItem = {
      id_card: props.item.id,
      id: Math.random((300 * 39)*10).toFixed(2),
      item_name: userInput.current.value,
      created_at: dateFormat,
      completed: false
    }
  

   dispatch({ type: "addItem", ItemBody: tempItem });
   let temItem2=item;
   temItem2.push(tempItem)
   setItem(temItem2)
    // console.log("item123->",item);
    //setItem([])
    
    

  }


    // const delTask = (_id) => {
    //   let temp = userDate.filter(item => item.id !== _id)
    //   setUserDate(temp);
    //   saveTolocal(temp)
    // }

    return (
      <div className="p-2 ">
        <h1 className="display-4 text-center" style={{ color: "turquoise" }}>{props.item.todo_name}</h1>
        <div className="d-flex align-items-center justify-content-center">
          <input ref={userInput} type="text" className="form-control w-25 border border-info" placeholder="I have to..." />
          <button onClick={addTask} className="btn btn-success">Add Item</button>
        </div>
        {item.map((val, i) => {
          return (
            <div key={i} className="d-flex justify-content-between align-items-center border border-danger p-2 m-2 items ">
              <div>
                <input id={val.id} type="checkbox" name="isDone" className="mr-1" checked={val.completed} />
                <label htmlFor={val.id}>{val.item_name}</label>
              </div>
              <div>{val.created_at}</div>
              {/* <div> */}
              <button onClick={() => { alert(val.id) }} className="btn btn-outline-secondary">X</button>
              {/* </div> */}
            </div>)

        })}
      </div>
    )
  }

  export default ToDoItem
