import Moment from 'moment';
let dateFormat = Moment(new Date()).format('DD/MM/YYYY');
const initState ={
    // dateFormat : new Date().toLocaleDateString(),
    task_ar:[
        { name: "Do homework", date: dateFormat },
        { name: "Go to work", date: dateFormat }
      ]
}


export const toDoReducer = (state = initState, action) => {
    if(action.type === "addTask"){
      let temp_ar = [...state.tasks_ar];
      temp_ar.push(action.taskItem);
      return {...state , tasks_ar:temp_ar}
    }
  
    else{
      return state;
    }
  }
