import React, { Component } from "react";

class Gallery extends Component {

    gallery_ar = ["beach-bridge.jpg","red-lights.jpg","up-code.jpg","earth.jpg"];
    next = () => {
        this.count++;
        this.forceUpdate();

    }
    back = () => {
        if (this.count <= 0) {
            this.count = 5;
        }
        else {
            this.count--;
        }
        this.forceUpdate();


    }
    render() {
        return (
            <div className="container border border-info">
                <img src ="./imgages/beach-bridge.jpg"/>
                <div className="row d-flex justify-content-center">
                    <div className="col-lg-6 p-2">
                        <button className="btn btn-warning" onClick={this.minus}>back</button>
                        <button className="btn btn-primary" onClick={this.Plus}>next</button>
                    </div>
                </div>
            </div>

        )
    }
}

export default Gallery;
