import React, { useEffect, useRef, useState } from 'react';
import { useDispatch } from 'react-redux';
import "../css_comps/todos.css"
import Moment from 'moment';
import { doApiGet, doApiPost } from '../services/apiService';
function ToDoItem(props) {
  let userInput = useRef(null);
  let dateFormat = Moment(new Date()).format('DD/MM/YYYY');
  // let [items, setItem] = useState(props.cardTodo.items)
  let items = props.cardTodo.items

  console.log("props.cardTodo.items->",props.cardTodo.items," ");
  console.log("props.cardTodo.items->",props.cardTodo.items[0]._id);
  

  let dispatch = useDispatch();
  const addTask = async () => {

    let url = `http://localhost:3000/todos/${props.cardTodo._id}/addItem/`
    let body = {
      item_name: userInput.current.value
    }

    doApiPost(url, body)
      .then(() => {
        let url = "http://localhost:3000/todos";
        doApiGet(url)
          .then(data => {
            console.log("data for DB", data);
            dispatch({ type: "updateList", data: data })
          });
      })
    userInput.current.value = "";


    // if (userInput.current.value === "") {
    //   alert("Please item name");

    // }
    // else {
    //   let tempItem = {
    //     indexCard: props.indexCard,
    //     id: Math.random((300 * 39) * 10).toFixed(2),
    //     item_name: userInput.
    //     indexcurrent.value,
    //     created_at: dateFormat,
    //     completed: false
    //   }

    //   dispatch({ type:"addItem", ItemBody: tempItem })
    //   console.log("items123->", items);
    //   userInput.current.value = "";
    // }
  }

  const checkedItem = (_val,_i) => {
    let url =`http://localhost:3000/todos/${props.cardTodo._id}/updateStatus/${props.cardTodo.items[_i]._id}`;
    let body = {
      completed: !_val
  }
 

  doApiPost(url,body)
  .then(data=>{
    let url = "http://localhost:3000/todos";
    doApiGet(url)
      .then(data => {
        console.log("data for DB", data);
        dispatch({ type: "updateList", data: data })
      });

  })

  }
  // const checkedItem = (val) => {
  //   items[val].completed = !items[val].completed;
  //   let obj = {
  //     item: items,
  //     indexCard: props.indexCard
  //   }
  //   dispatch({ type: "changeCompleted", itemChack: obj })

  // }


  const deleteItem = () => {
    let url = `http://localhost:3000/todos/${props.cardTodo._id}/deleteItem/${props.cardTodo.items._id}`;
    doApiPost(url)
      .then(data => {
        console.log(data);
        let url = "http://localhost:3000/todos";
        doApiGet(url)
          .then(data => {
            console.log("data for DB", data);
            dispatch({ type: "updateList", data: data })
          });

      })
  
  }

  // const delTask = (itemId) => {
  //   let obj = {
  //     itemId: itemId,
  //     indexCard: props.indexCard
  //   }
  //   dispatch({ type: "delTask", itemDelete: obj })

  // }

  return (
    <div className="p-2 ">
      <h1 className="display-4 text-center text-white" >{props.cardTodo.todo_name}</h1>
      <div className="d-flex align-items-center justify-content-center inputItem">
        <input ref={userInput} type="text" className="form-control  border border-info " placeholder="I have to..." />
        <button onClick={addTask} className="btn btn-info">Add</button>
      </div>
      {items.map((val, i) => {
        return (
          <div key={i} className="d-flex justify-content-between align-items-center p-2 mt-1 items ">
            <div>
              <input id={val.id} type="checkbox" name="isDone" onClick={() => {
                checkedItem(val.completed,i)
              }} className="mr-1" checked={val.completed} />
              <label htmlFor={val.id}>{val.item_name}</label>
            </div>
            <div>{val.created_at}</div>
            <button onClick={() => { deleteItem() }} className="btn btn-danger">X</button>
            {/* <button onClick={() => { delTask(i) }} className="btn btn-danger">X</button> */}

          </div>)

      })}
    </div>
  )
}

export default ToDoItem
