import React from 'react';
import { useSelector } from 'react-redux';
import { useHistory } from 'react-router-dom';
import "../css_comps/todos.css"
function TodoCard(props) {
    let history = useHistory();
    let card = useSelector((myStore) => myStore.card)
    const addTask = () => {
        history.push(`/addItems/${props.item.id}`)
    }
    return (
        <div  className=" bg-success w-100">
            <div onClick={addTask} className="d-flex align-items-center justify-content-centerp-3  mb-3 list border border-danger">
                {props.item.todo_name}
            </div>
        </div>
    )
}

export default TodoCard
