import React,{Component} from 'react';

class UserInput extends Component{
    changeDate = () => {
      // alert()
      this.props.updateDate2(this.refs.id_input.value)
    }
  
    render(){
      return(
        <div className="d-flex justify-content-center">
          <div className="col-lg-6 border p-2 d-flex  justify-content-center">
            <input ref="id_input" type="date" className="form-control w-50"/>
            <button onClick={this.changeDate} className="btn btn-secondary">change date</button>
          </div>
        </div> 
      )
  }
  }
  
  export default UserInput
