import React,{Component} from "react";

class ShowDateDays extends Component{
    state= {date:"99"}
    componentDidMount(){
        console.log("did mount");
        this.updateDays(this.props.date)
        
    }
    componentDidUpdate(_prevProps,prevState){
        console.log("did update");
        
        if(_prevProps.date != this.props.date){
            this.updateDays(this.props.date)

        }
        
    }

    updateDays = (_newDate) =>{
        let time = Date.parse(_newDtae)-Date.parse(new Date());
        let days1 =Math.floor(time/(1000*60*60*24));
        this.setState({days:days1})
    }
  render(){
    return(
        <div className="text-center">
        <h2>CountDown to: {this.props.date}</h2>
    <h3>Days: {this.state.date}</h3>
      </div> 

    
    )
  }
}

export default ShowDateDays;
