import React, { Component } from "react";

class InputData extends Component {
  state = { bgH2: "white", fontColor: "black" }

  id_input = React.createRef();
  id_select = React.createRef();
  onChangeColor = () => {
    //console.log(this.id_input.current.value)
    this.setState({ bgH2: this.id_input.current.value })
    //alert("clicked")
  }

  onChageFont = () => {
    this.setState({fontColor:this.id_select.current.value})
  }

  render() {
    return (
      <div className="col-lg-6">
        <h2 style={{
          background: this.state.bgH2,
          color: this.state.fontColor
        }}>
          Enter your favorite color:
            </h2>
        <input ref={this.id_input} type="text" className="form-control" />
        <button onClick={this.onChangeColor}>Change color</button>
        <br />
        <select ref={this.id_select} onChange={this.onChageFont}>
          <option value="red">Red</option>
          <option value="blue">blue</option>
          <option value="green">green</option>
        </select>
      </div>

    )
  }
}

export default InputData;
