        return (
            <div className="container border border-info mt-4">
                <h1>Exchange</h1>
                <div className="row">
                    <div className="col-lg 6">
                        <h2>Enter the currency you want to exchange:</h2>
                        <select id="id_select" onChange="exchange()" className = "w-25 h-20">
                            <option value="3.4508">USD</option>
                            <option value="3.8571">EUR</option>
                            <option value="0.1113">THB</option>
                        </select><h2>Enter the amount you want:</h2>
                        <input type="number" min="1" id="id_input" oninput="exchangeOninput()" />
                        <h2 id="resultField">It will cost you XXX NIS</h2>

                    </div>

                </div>

            </div>

        )
    }
}

export default Exchange;
