import React, { Component } from "react";
import SearchPixa from "./searchPixa";
import PixaDisplay from "./pixaDisplay";
import { doApi } from "../service/apiSerGet";

class AppPixa extends Component {
  state = { _ar: [] };

  componentDidMount() {
    let url = "http://fs1.co.il/bus/pixa1.php";
    doApi(url)
      .then(data => {
        console.log("pixa_ar", data);
        this.setState({ _ar: data });
      })
  }

  render() {
    return (
      <div className="contanier text-center">
        <h1>Pixa search</h1>
        <div className="row">
          <SearchPixa />
          {this.state._ar.map(item => {
            return (
              <PixaDisplay item={item} key={item.id} />

            )

          })}

        </div>

      </div>

    )
  }
}

export default AppPixa;
