import React, { useRef, useState } from 'react';
function TodoTitle(props){
  let todoName =useRef(null);
  const createTodoTitle =()=>{
    alert(todoTitle.current.value)
  }
  return(
    <div className="container text-center">  
     <div className="d-flex align-items-center justify-content-center">
        <input ref={todoName} type="text" className="form-control w-25 border border-info" placeholder="I have to..." />
        <button onClick={createTodoTitle} className="btn btn-success">Add Item</button>
      </div>
    </div> 
  )
}

export default TodoTitle
