import React, { Component } from "react";

class Gallery extends Component {
   
    i=0;

    gallery_ar = ["beach-bridge.jpg","red-lights.jpg","up-code.jpg","earth.jpg"];
    image = 
    [
        "https://images1.calcalist.co.il/PicServer2/20122005/305274/shutterstock_2283577372767_.jpg",     
        "https://www.m-y-net.co.il/uploads/n/1588003204.3509.jpg",     
        "https://upload.wikimedia.org/wikipedia/commons/4/4e/Macaca_nigra_self-portrait_large.jpg",
        "https://a7.org/pictures/860/860357.jpg"     
    ]

    next = () => {
        this.i++;
        this.forceUpdate();

    }
    back = () => {
        if (this.i <= 0) {
            this.i = 3;
            // this.gallery_ar[this.i]
        }
        else {
            this.i--;
            // this.gallery_ar[this.i]
        }
        this.forceUpdate();


    }
    render() {
        return (
            <div className="container border border-info">
                {/* <img src ={this.image[this.i%4]} className = "w-25"/> */}
                
                 <img  src={require(`../images/${this.gallery_ar[this.i]}`)} />
                <div className="row d-flex justify-content-center">
                    <div className="col-lg-6 p-2">
                        <button className="btn btn-warning" onClick={this.back}>back</button>
                        <button className="btn btn-primary" onClick={this.next}>next</button>
                    </div>
                </div>
            </div>

        )
    }
}

export default Gallery;
