import React,{Component} from "react";
import ShowDateDays from "./showDateDays";
import UserInput from "./userInput";

class CountDownApp extends Component{
  state ={date:"2020-01-01"}
  update =(_newDate)=>{
    this.setState({date:_newDate})
  }
  render(){
    return(
      <div>
        <ShowDateDays date={this.state.date}/>
        <UserInput updateDate2 = {this.update}/>
      </div>
    
    )
  }
}

export default CountDownApp;
