// import Moment from 'moment';
// let dateFormat = Moment(new Date()).format('DD/MM/YYYY');
const initState = {
  item:{
    id: Math.random(300 * 39).toFixed(2),
    item_name: "homework",
    created_at: "26/12/2020",
    completed:false,
  },
  card:{
    id: 1,
    todo_name: "todos shopping list",
    items: [
      {
      id: Math.random(300 * 39).toFixed(2),
      item_name: "homework",
      created_at: "26/12/2020",
      completed:false,
    },
    {
      id: Math.random(300 * 39).toFixed(2),
      item_name: "googo ",
      created_at: "26/12/2020",
      completed:false,
    }],
    created_at: "26/12/2020",
    updated_at: "27/12/2020"
  },
  todosList_ar: [
    {
      id: 1,
      todo_name: "todos shopping list",
      items: [
        {
        id: Math.random(300 * 39).toFixed(2),
        item_name: "homework",
        created_at: "26/12/2020",
        completed:false,
      },
      {
        id: Math.random(300 * 39).toFixed(2),
        item_name: "googo ",
        created_at: "26/12/2020",
        completed:true,
      }],
      created_at: "26/12/2020",
      updated_at: "27/12/2020"
    },
    {
      id: 2,
      todo_name: "buy cars",
      items: [
        {
        id: Math.random(300 * 39).toFixed(2),
        item_name: "go to trip arond the world",
        created_at: "26/12/2020",
        completed:true,
      },
      {
        id: Math.random(300 * 39).toFixed(2),
        item_name: "lensting to music list",
        created_at: "26/12/2020",
        completed:false,
      }],
      created_at: "26/12/2020",
      updated_at: "27/12/2020"
    }
  ],
  counterId: 3
}


export const toDoReducer = (state = initState, action) => {
  if (action.type === "addTodoCrad") {
    let temp_ar = [...state.todosList_ar];
    temp_ar.push(action.todoCrad);
    state.counterId++;
    return { ...state, todosList_ar: temp_ar}
  }
  // else if (action.type === "addCounterId") {
  //   state.counterId++;
  //   return { ...state, counterId: state.counterId++ }

  // }

  else {
    return state;
  }
}
