import React,{Component} from "react";

class InputData extends Component{
  state = {bgH2:"white"}

  id_input = React.createRef()
  onChangeColor = () => {
    //console.log(this.id_input.current.value)
    this.setState({bgH2:this.id_input.current.value})
    //alert("clicked")
  }

  render(){
    return(
      <div className="col-lg-6 mb-4">
        <h2 style={{background:this.state.bgH2}}>Enter your favorite color:</h2>
        <input ref={this.id_input} type="text" className="form-control"/>
        <button onClick={this.onChangeColor}>Change color</button>
      </div>
    
    )
  }
}

export default InputData;
